mapper.update:
/** MyMembershipMapper.rejoin */
UPDATE TB_MY_MEMBERSHIP
   SET STATUS = '1'
     , WITHDRAWAL = NULL
 WHERE ACCOUNT_ID = #{accountId}
   AND MSP_ID = #{mspId}

/** MyMembershipMapper.withdrawal */
UPDATE TB_MY_MEMBERSHIP
SET STATUS = '0'
, WITHDRAWAL_DATE = NOW()
WHERE ACCOUNT_ID = #{accountId}
AND MSP_ID = #{mspId}

mapper.select:
/** MyMembershipMapper.findById */
SELECT A.*
  FROM TB_MY_MEMBERSHIP A
 WHERE 1=1
   AND A.ACCOUNT_ID = #{accountId}
   AND A.MSP_ID = #{mspId}
